import React,{Component} from 'react'
import TextField from '@mui/material/TextField';
import CreateIcon from '@mui/icons-material/Create';
import { Button } from '@mui/material';
import CurrentDT from './CurrentDT';

class Create extends Component{
  constructor(props) {
    super(props)
    this.state = {
      blogs : ["Good morning"],
      new_blog : "",
      pub : [new Date().toDateString]
    }
  }
  changeValue = (e) =>{
    this.setState({new_blog:e.target.value})
  }

  clickButton = (e) =>{
    e.preventDefault()
    this.setState({blogs : [...this.state.blogs,this.state.new_blog]})
    this.setState({new_blog: ""})
    console.log(this.state.pub)

  }


  render(){
    return(
      <>
     <h2>  <CreateIcon/> Write it and Post it...!</h2>

      <TextField
             sx={{

              width: { sm: 300, md: 700 },
              "& .MuiInputBase-root": {
                  height: 100
              }
          }}
          fullWidth
          size="Large"
          id="outlined-multiline-static"
          label="Create your blog "
          multiline  
          rows={4}
          value={this.state.new_blog}
          onChange={this.changeValue}
        />
        <Button 
          sx={{
            "&:hover":{
              backgroundColor: 'blue'
            }
          }}

        onClick={this.clickButton} size="small " variant="contained" color="secondary">
          Post 
        </Button>

      <ul>
        {
          this.state.blogs.map(elem =>(
            <p>
              <li>{elem}</li> <CurrentDT />
              </p>
          ))
        }
      </ul>

      </>
    )
  }
}

export default Create
